import React, { useState, useEffect, useRef, useCallback } from 'react';
import logo from './logo.svg';
import './App.css';
import * as MUI from '@mui/material';


function AlertComponent(props) {
    return (
        <MUI.Alert severity="error">
            <MUI.AlertTitle>Error</MUI.AlertTitle>
            This is an error alert - <strong>check it out!</strong>
        </MUI.Alert>
    );
}

function Timer() {
    const Ref = useRef(null);
    const [timer, setTimer] = useState('00:01:00');
    const [isRunning, setIsRunning] = useState(true);
    const [endTime, setEndTime] = useState(() => {
      let deadline = new Date();
      deadline.setSeconds(deadline.getSeconds() + 60);
      return deadline;
    });

    const getTimeRemaining = (e) => {
      const total = Date.parse(e) - Date.parse(new Date());
      const seconds = Math.floor((total / 1000) % 60);
      const minutes = Math.floor((total / 1000 / 60) % 60);
      const hours = Math.floor((total / 1000 / 60 / 60) % 24);
      return {
        total, hours, minutes, seconds
      };
    };

    const startTimer = useCallback((e) => {
      let { total, hours, minutes, seconds } = getTimeRemaining(e);
      if (total >= 0) {
        setTimer(
          (hours > 9 ? hours : '0' + hours) + ':' +
          (minutes > 9 ? minutes : '0' + minutes) + ':' +
          (seconds > 9 ? seconds : '0' + seconds)
        );
      }
    }, []);

    const clearTimer = useCallback(() => {
      if (isRunning) {
        if (Ref.current) clearInterval(Ref.current);
        const id = setInterval(() => {
          startTimer(endTime);
        }, 1000);
        Ref.current = id;
      } else {
        if (Ref.current) clearInterval(Ref.current);
      }
    }, [isRunning, endTime, startTimer]);

    const toggleTimer = () => {
        if (isRunning) {
          const currentTime = new Date();
          const remainingTime = getTimeRemaining(endTime).total;
          setEndTime(new Date(currentTime.getTime() + remainingTime));
        }
        setIsRunning((prevIsRunning) => !prevIsRunning);
    };


    useEffect(() => {
      clearTimer();
      return () => {
        if (Ref.current) clearInterval(Ref.current);
      };
    }, [clearTimer]);

    const onClickReset = () => {
      setIsRunning(false);
      setTimer('00:01:00');
      const deadline = new Date();
      deadline.setSeconds(deadline.getSeconds() + 60);
      setEndTime(deadline);
      setIsRunning(true);
    };

    return (
      <div className="App">
        <p className="timer">{timer}</p>
        <button onClick={toggleTimer}>{isRunning ? 'Pause' : 'Play'}</button>
        <button onClick={onClickReset}>Reset</button>
      </div>
    );
  }

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Timer></Timer>
      </header>
    </div>
  );
}

export default App;
